mapper.select:
SELECT *
FROM
(
SELECT
`a`.`CUSTOMER_ID` AS `customerId`,
`a`.`CUSTOMER_TOKEN` AS `openId`,
`a`.`CUSTOMER_NICKNAME` AS
`nickName`,
ifnull(
`a`.`CUSTOMER_REALNAME`,
`a`.`CUSTOMER_PARAM9`
) AS
`realName`,
`a`.`CUSTOMER_SEX` AS `gender`,
`a`.CUSTOMER_PHONE AS
`iphone`,
`a`.`CUSTOMER_PARAM10` AS `idCard`,
(
date_format(now(), '%Y')
- substr(`a`.`CUSTOMER_PARAM10`, 7, 4)
) AS `age`,
`v_order`.`total_amount` AS `totalAmount`,
`v_order`.`num_of_orders` AS
`orders`,
`v_order`.recent_order as `recentOrder`
FROM
`v_order_view`
`v_order`
LEFT JOIN `e_customer_info` `a` ON (
(
`a`.`CUSTOMER_ID` =
CONVERT (
`v_order`.`CUSTOMER_ID` USING utf8mb4
)
)
)

where 1=1

	AND CUSTOMER_SEX = #{filter.sex}



	AND CUSTOMER_PHONE = #{filter.phone}



	AND CUSTOMER_IDCARD_NO = #{filter.idCard}



	AND total_amount >=
	#{filter.amountST}



	AND total_amount <=
	#{filter.amountST}



	AND (date_format(now(), '%Y') - substr(`a`.`CUSTOMER_PARAM10`, 7, 4)) >=
	#{filter.ageST}



	AND (date_format(now(), '%Y') - substr(`a`.`CUSTOMER_PARAM10`, 7, 4)) <=
	#{filter.ageED}



	AND recent_order >=
	#{filter.orderST}



	AND recent_order <=
	#{filter.orderED}


LIMIT #{count} OFFSET #{offset}
) tableA

ORDER BY
tableA.${OrderModel.orderBy} ${OrderModel.sort}

SELECT count(*)
FROM
`v_order_view` `v_order`
LEFT JOIN `e_customer_info`
`a` ON (
(
`a`.`CUSTOMER_ID` = CONVERT (
`v_order`.`CUSTOMER_ID` USING
utf8mb4
)
)
)

where 1=1

	AND CUSTOMER_SEX = #{filter.sex}



	AND CUSTOMER_PHONE = #{filter.phone}



	AND CUSTOMER_IDCARD_NO = #{filter.idCard}



	AND total_amount >=
	#{filter.amountST}



	AND total_amount <=
	#{filter.amountST}



	AND (date_format(now(), '%Y') - substr(`a`.`CUSTOMER_PARAM10`, 7, 4)) >=
	#{filter.ageST}



	AND (date_format(now(), '%Y') - substr(`a`.`CUSTOMER_PARAM10`, 7, 4)) <=
	#{filter.ageED}



	AND recent_order >=
	#{filter.orderST}



	AND recent_order <=
	#{filter.orderED}
